// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router'
import Home from '@/components/Home.vue'
import Auth from '@/components/Auth.vue'
import Register from '@/components/Register.vue'
import ExpensesList from '@/components/ExpensesList.vue'
import ExpenseCreateUpdate from '@/components/ExpenseCreateUpdate.vue'
import BudgetsList from '@/components/BudgetsList.vue'
import BudgetCreateUpdate from '@/components/BudgetCreateUpdate.vue'

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    meta: { requiresAuth: true } // Protected route
  },
  {
    path: '/about',
    name: 'About',
    //component: () => import('@/components/About.vue')
  },
  {
    path: '/contact',
    name: 'Contact',
    //component: () => import('@/components/Contact.vue')
  },
  {
    path: '/privacy',
    name: 'Privacy',
    //component: () => import('@/components/Privacy.vue')
  },
  {
    path: '/expenses',
    name: 'ExpensesList',
    component: () => import('@/components/ExpensesList.vue')
  },
  {
    path: '/expenses/:id?', // Optional ID for edit
    name: 'ExpenseCreateUpdate',
    component: ExpenseCreateUpdate,
    meta: { requiresAuth: true }
  },
  {
    path: '/expenses/create',
    name: 'ExpenseCreate',
    component: () => import('@/components/ExpenseCreateUpdate.vue')
  },
  {
    path: '/expenses/edit/:id',
    name: 'ExpenseEdit',
    component: () => import('@/components/ExpenseCreateUpdate.vue')
  },
  {
    path: '/budgets',
    name: 'BudgetsList',
    component: BudgetsList,
    meta: { requiresAuth: true }
  },
  {
    path: '/budgets/:id?', // Optional ID for edit
    name: 'BudgetCreateUpdate',
    component: BudgetCreateUpdate,
    meta: { requiresAuth: true }
  },
  {
    path: '/auth',
    name: 'Auth',
    component: Auth
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import('@/components/Register.vue')
  },
  {
    path: '/users/create',
    name: 'UserCreate',
    component: () => import('@/components/UserCreate.vue')
  },
  {
    path: '/users/edit/:id',
    name: 'UserEdit',
    component: () => import('@/components/UserCreate.vue')
  },
  {
    path: '/users',
    name: 'UserList',
    component: () => import('@/components/UserList.vue')
  },
  {
    path: '/users/:msg',
    name: 'UserListMessage',
    component: () => import('@/components/UserList.vue')
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// Authentication guard
router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('access_token')

  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isAuthenticated) {
      next({ name: 'Auth' })
    } else {
      next()
    }
  } else {
    next()
  }
})

export default router
